mapper.select:
select
activityrecords.*,user.name as userName,activity.name as activityName
from activityrecords
left join user on activityrecords.user_id = user.id
left join activity on activityrecords.activity_id = activity.id

     and id = #{id}
     and activity_id = #{activityId}
     and user_id = #{userId}
     and time like concat('%', #{time}, '%')

order by id desc
